/* global $, console, analytics *//* Button Click Event */function fetchSession(e,t){var n=new FormData;n.append("document_id",e);n.append("expire",t);$.ajax({type:"POST",contentType:!1,processData:!1,data:n,dataType:"json",url:"sessions",error:function(e){console.log(e);$("#session-link").text("Something went wrong while converting...").show()},statusCode:{200:function(e){$("button, #progress").toggle();$("#session-link").text(e.session_url).attr("href",e.session_url).show()},202:function(){fetchSession(e,t)}}})}$("#choose-desktop-upload").click(function(){$("#session-link").hide();$("#desktop-upload-form").show();$("#url-upload-form").hide()});$("#choose-url-upload").click(function(){$("#session-link").hide();$("#url-upload-form").show();$("#desktop-upload-form").hide()});$("#desktop-upload-form").submit(function(){$("button").blur();var e=new FormData;e.append("file",$("#file_to_convert")[0].files[0]);var t=$("#desktop-expiration").is(":checked")?"no_expire":"expire";$("button, #progress").toggle("fast");$.ajax({type:"POST",contentType:!1,processData:!1,data:e,dataType:"json",url:"desktop-upload",error:function(e){console.log(e);$("#session-link").text("Something went wrong while converting...").show();analytics.track("Desktop Conversion",{success:"false"})}}).done(function(e){fetchSession(e.id,t);analytics.track("Desktop Conversion",{success:"true"})});return!1});$("#url-upload-form").submit(function(){$("button").blur();var e=new FormData;e.append("document-url",$("#file-url").val());var t=$("#url-expiration").is(":checked")?"no_expire":"expire";$("button, #progress").toggle("fast");$.ajax({type:"POST",contentType:!1,processData:!1,data:e,dataType:"json",url:"url-upload",error:function(e){console.log(e);$("#session-link").text("Something went wrong while converting...").show();analytics.track("URL Conversion",{success:"false"})}}).done(function(e){fetchSession(e.id,t);analytics.track("URL Conversion",{success:"true"})});return!1});